INSERT INTO `product` VALUES (5, '', 'Mirage CVT', '', 'mirage-cvt', '', '', '', '', 'Mitsubishi Mirage từng xuất hiện tại triển lãm ôtô Việt Nam 2012 với mục đích thăm dò ý kiến của người tiêu dùng về giá bán cũng như trang thiết bị tiện nghi và có lẽ với những phản ứng tích cực, hãng xe Nhật đã quyết định tham gia phân khúc xe cỡ nhỏ ở Việt Nam.', '', '', '<div class="slide" data-easyparallax-group="true" data-easyparallax-specifychild="true" data-slide="02" id="content2" style="margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: Arial; line-height: 16px; text-align: center;">\r\n	<div class="product_content" style="margin: 0px auto; padding: 0px; width: 980px; text-align: left;">\r\n		<h1 class="title_mirage mT30" style="margin: 30px 0px 0px; padding: 0px; font-size: 12px; font-weight: normal;">\r\n			<span style="margin: 0px; padding: 0px; font-size: 20px; color: rgb(93, 193, 36); font-family: Arimo; line-height: 35px; font-weight: bold; text-transform: uppercase; border-bottom-color: rgb(93, 193, 36); border-bottom-style: solid; border-bottom-width: 2px;">NỔI BẬT</span></h1>\r\n		<div class="clear" style="margin: 0px; padding: 0px; clear: both;">\r\n			<img alt="" height="35" src="http://www.vinastarmotors.com.vn/images/spacer.gif" /></div>\r\n		<div class="hightlights_content" style="margin: 0px; padding: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(230, 230, 230);">\r\n			<img alt="Nổi bật" class="right" data-easyparallax-animationend="0.1" data-easyparallax-animationstart="0.4" data-easyparallax-child="true" src="http://www.vinastarmotors.com.vn/images/hightlight_1.jpg" style="float: right;" title="Nổi bật" />\r\n			<div class="highlights_mirage_vn" style="margin: 0px; padding: 0px; font-size: 33px; color: rgb(93, 193, 36); font-family: Arimo; line-height: 45px; text-transform: uppercase;">\r\n				<a class="scroll_cvn" data-speed="1300" href="http://www.vinastarmotors.com.vn/vn/mirage.php#content5" style="text-decoration: none; color: rgb(98, 193, 41); outline: none;">1.TI&Ecirc;U HAO NHI&Ecirc;N LIỆU TỐT&nbsp;&nbsp;NHẤT PH&Acirc;N KH&Uacute;C:<br />\r\n				4,7 L&Iacute;T/100KM</a></div>\r\n			<br />\r\n			<p align="justify" data-easyparallax-child="true" style="margin: 0px; padding: 0px 0px 0px 30px; line-height: 20px;">\r\n				&bull; Động cơ 1.2L MIVEC thế hệ mới<br />\r\n				&bull; Hộp số CVT với nhiều cải tiến<br />\r\n				&bull; Sử dụng vật liệu mới trong thiết kế th&acirc;n xe để tối ưu trọng lượng</p>\r\n			<div class="clear" style="margin: 0px; padding: 0px; clear: both;">\r\n				<img alt="" height="30" src="http://www.vinastarmotors.com.vn/images/spacer.gif" /></div>\r\n		</div>\r\n		<div class="clear" style="margin: 0px; padding: 0px; clear: both;">\r\n			<img alt="" height="30" src="http://www.vinastarmotors.com.vn/images/spacer.gif" /></div>\r\n		<div class="hightlights_content" style="margin: 0px; padding: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(230, 230, 230);">\r\n			<img alt="Dễ điều khiển" class="right" data-easyparallax-animationend="0.1" data-easyparallax-animationstart="0.9" data-easyparallax-child="true" src="http://www.vinastarmotors.com.vn/images/hightlight_2.jpg" style="float: right; margin-left: 40px;" title="Dễ điều khiển" />\r\n			<div class="highlights_mirage_vn" style="margin: 0px; padding: 0px; font-size: 33px; color: rgb(93, 193, 36); font-family: Arimo; line-height: 45px; text-transform: uppercase;">\r\n				<a class="scroll_cvn" data-speed="1300" href="http://www.vinastarmotors.com.vn/vn/mirage.php#highlight_link_2" style="text-decoration: none; color: rgb(98, 193, 41); outline: none;">2.DỄ ĐIỀU KHIỂN</a></div>\r\n			<br />\r\n			<p align="justify" data-easyparallax-child="true" style="margin: 0px; padding: 0px 0px 0px 30px; line-height: 20px;">\r\n				&bull; B&aacute;n k&iacute;nh quay v&ograve;ng nhỏ nhất ph&acirc;n kh&uacute;c (4,6m)<br />\r\n				&bull; Tầm quan s&aacute;t tốt từ vị tr&iacute; l&aacute;i nhờ v&agrave;o cột chữ A nhỏ<br />\r\n				&bull; Tay l&aacute;i trợ lực điện: nhẹ nh&agrave;ng ở tốc độ thấp v&agrave; ổn định ở tốc độ cao</p>\r\n			<div class="clear" style="margin: 0px; padding: 0px; clear: both;">\r\n				<img alt="" height="30" src="http://www.vinastarmotors.com.vn/images/spacer.gif" /></div>\r\n		</div>\r\n		<div class="clear" style="margin: 0px; padding: 0px; clear: both;">\r\n			<img alt="" height="30" src="http://www.vinastarmotors.com.vn/images/spacer.gif" /></div>\r\n		<div class="hightlights_content" style="margin: 0px; padding: 0px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(230, 230, 230);">\r\n			<img alt="Tiện nghi vượt trội" class="right" data-easyparallax-animationstart="0.4" data-easyparallax-child="true" src="http://www.vinastarmotors.com.vn/images/hightlight_3.jpg" style="float: right;" title="Tiện nghi vượt trội" />\r\n			<div class="highlights_mirage_vn" style="margin: 0px; padding: 0px; font-size: 33px; color: rgb(93, 193, 36); font-family: Arimo; line-height: 45px; text-transform: uppercase;">\r\n				<a class="scroll_cvn" data-speed="1300" href="http://www.vinastarmotors.com.vn/vn/mirage.php#content4" style="text-decoration: none; color: rgb(98, 193, 41); outline: none;">3.TIỆN NGHI VƯỢT TRỘI</a></div>\r\n			<br />\r\n			<p align="justify" data-easyparallax-child="true" style="margin: 0px; padding: 0px 0px 0px 30px; line-height: 20px;">\r\n				&bull; Hệ thống KOS với n&uacute;t bấm khởi động<br />\r\n				&bull; Điều h&ograve;a tự động<br />\r\n				&bull; DVD với m&agrave;n h&igrave;nh LCD, kết nối Bluetooth/ USB/ AUX</p>\r\n			<div class="clear" style="margin: 0px; padding: 0px; clear: both;">\r\n				<img alt="" height="30" src="http://www.vinastarmotors.com.vn/images/spacer.gif" /></div>\r\n		</div>\r\n		<div class="clear" style="margin: 0px; padding: 0px; clear: both;">\r\n			<img alt="" height="30" src="http://www.vinastarmotors.com.vn/images/spacer.gif" /></div>\r\n		<d