INSERT INTO `product` VALUES (9, '', 'Attrage MT STD', '', 'attrage-mt-std', '468000000', '', 'upload/images/attrage-mt-std.png', '', '', '', '', '<div class="proDetail-Content" style="margin: 0px; padding: 0px; color: rgb(101, 101, 101); font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; text-align: justify;">\r\n	<div style="margin: 0px; padding: 0px; box-sizing: border-box; border: 0px; font-family: Arial, Helvetica, sans-serif; outline: 0px; vertical-align: baseline; color: rgb(64, 64, 64); line-height: 18px;">\r\n		<p style="margin: 0px 0px 1.5em; padding: 0px; line-height: 1.3; box-sizing: border-box; border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline;">\r\n			<strong style="margin: 0px; padding: 0px; box-sizing: border-box; border: 0px; font-style: inherit; outline: 0px; vertical-align: baseline;"><span style="margin: 0px; padding: 0px; box-sizing: border-box; border: 0px; font-family: ''times new roman'', times, serif; font-size: medium; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline;">Mitsubishi Attrage l&agrave; d&ograve;ng sản phẩm mới của Mitsubishi Việt Nam, d&ograve;ng xe Sedan 5 chỗ nhập khẩu nguy&ecirc;n chiếc từ Th&aacute;i Lan với nhiều trang thiết bị tiện nghi hiện đại mang đến cho bạn v&agrave; gia đ&igrave;nh những trải nghiệm th&uacute; vị. Mitsubishi Attrage l&agrave; d&ograve;ng xe to&agrave;n cầu của Mitsubishi đ&atilde; được ph&acirc;n phối tại c&aacute;c thị trường Mỹ, Nhật, Th&aacute;i Lan&hellip;mang phong c&aacute;ch thiết kế mới với những đường n&eacute;t sang trọng, th&acirc;n thiện với m&ocirc;i trường v&agrave; t&iacute;nh kh&iacute; động học cao.</span></strong></p>\r\n		<p style="margin: 0px 0px 1.5em; padding: 0px; line-height: 1.3; box-sizing: border-box; border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline;">\r\n			<img alt="" src="http://media.bizwebmedia.net/sites/34134/data/Upload/2014/11/img01png_20141023095008169(3).png" style="margin: 0px; padding: 0px; border: 0px; width: 704px; height: 314px;" /></p>\r\n	</div>\r\n</div>\r\n<div class="proDetail-img" style="margin: 0px; padding: 0px; color: rgb(101, 101, 101); font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; text-align: justify;">\r\n	<b style="margin: 0px; padding: 0px; color: rgb(20, 20, 20); font-family: Arial, Helvetica, ''Times New Roman'', Times, serif; font-size: 15px; line-height: 24px; background-color: rgb(252, 252, 255);">Trong thời gian tới, ngo&agrave;i mẫu Mitsubishi Outlander Sport, Vina Star Motors (VSM) sẽ giới thiệu ra thị trường Việt Nam mẫu Sedan&nbsp;hạng B Attrage, chiếc xe n&agrave;y sẽ cạnh tranh trực tiếp với Toyota Vios, Honda City&nbsp;hay Huyndai Accent.</b><br style="margin: 0px; padding: 0px; color: rgb(20, 20, 20); font-family: Arial, Helvetica, ''Times New Roman'', Times, serif; font-size: 15px; line-height: 24px; background-color: rgb(252, 252, 255);" />\r\n	<br style="margin: 0px; padding: 0px; color: rgb(20, 20, 20); font-family: Arial, Helvetica, ''Times New Roman'', Times, serif; font-size: 15px; line-height: 24px; background-color: rgb(252, 252, 255);" />\r\n	<span style="margin: 0px; padding: 0px; color: rgb(20, 20, 20); font-family: Arial, Helvetica, ''Times New Roman'', Times, serif; font-size: 15px; line-height: 24px; background-color: rgb(252, 252, 255);">VSM cũng tiết lộ Attrage sẽ c&oacute; mức gi&aacute; dưới 600 triệu đồng, hứa hẹn sẽ đem lại sự đa dạng lựa chọn hơn nữa cho người ti&ecirc;u d&ugrave;ng. B&ecirc;n cạnh đ&oacute;, c&aacute;c mẫu xe hiện tại như Mirage, Pajero Sport, Triton pick-up sẽ được n&acirc;ng cấp trong thời gian tới.</span><br style="margin: 0px; padding: 0px; color: rgb(20, 20, 20); font-family: Arial, Helvetica, ''Times New Roman'', Times, serif; font-size: 15px; line-height: 24px; background-color: rgb(252, 252, 255);" />\r\n	<br style="margin: 0px; padding: 0px; color: rgb(20, 20, 20); font-family: Aria