INSERT INTO `product` VALUES (12, '', 'Trion GLX', '', 'trion-glx', '576000000', '', 'upload/images/triton-glx.jpg', '', 'Mitsubishi Triton là mẫu xe được thiết kế hiện đại với đường nét thể thao, được thừa hưởng những công nghệ tiên tiến được áp dụng cho những mẫu xe Mitsubishi trên đường đua Paris-Dakar. Nội thất rộng rãi và thanh lịch với những trang thiết bị an toàn như dòng xe du lịch nhưng vẫn có phần thùng chuyên chở hàng hóa, vật dụng tiện ích rộng rãi phía sau.', '', '', '<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	Mitsubishi Triton l&agrave; mẫu xe được thiết kế hiện đại với đường n&eacute;t thể thao, được thừa hưởng những c&ocirc;ng nghệ ti&ecirc;n tiến được &aacute;p dụng cho những mẫu xe Mitsubishi tr&ecirc;n đường đua Paris-Dakar. Nội thất rộng r&atilde;i v&agrave; thanh lịch với những trang thiết bị an to&agrave;n như d&ograve;ng xe du lịch nhưng vẫn c&oacute; phần th&ugrave;ng chuy&ecirc;n chở h&agrave;ng h&oacute;a, vật dụng tiện &iacute;ch rộng r&atilde;i ph&iacute;a sau. Cũng như c&aacute;c mẫu xe Mitsubishi kh&aacute;c, Triton đạt được t&iacute;nh năng vận h&agrave;nh h&agrave;ng đầu c&ugrave;ng với sự bền bỉ v&agrave; tin cậy so với c&aacute;c đối thủ c&ugrave;ng loại.</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	&nbsp;</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	&nbsp;</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<div align="justify" style="margin: 0px; padding: 0px;">\r\n		<img alt="" height="453" src="data:image/jpeg;base64,', '', '18', 14);
INSERT INTO `product` VALUES (13, '', 'Trion GL', '', 'trion-gl', '530000000', '', 'upload/images/triton_GL.jpg', '', 'Mitsubishi Triton là mẫu xe được thiết kế hiện đại với đường nét thể thao, được thừa hưởng những công nghệ tiên tiến được áp dụng cho những mẫu xe Mitsubishi trên đường đua Paris-Dakar. Nội thất rộng rãi và thanh lịch với những trang thiết bị an toàn như dòng xe du lịch nhưng vẫn có phần thùng chuyên chở hàng hóa, vật dụng tiện ích rộng rãi phía sau.', '', '', '<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	Mitsubishi Triton l&agrave; mẫu xe được thiết kế hiện đại với đường n&eacute;t thể thao, được thừa hưởng những c&ocirc;ng nghệ ti&ecirc;n tiến được &aacute;p dụng cho những mẫu xe Mitsubishi tr&ecirc;n đường đua Paris-Dakar. Nội thất rộng r&atilde;i v&agrave; thanh lịch với những trang thiết bị an to&agrave;n như d&ograve;ng xe du lịch nhưng vẫn c&oacute; phần th&ugrave;ng chuy&ecirc;n chở h&agrave;ng h&oacute;a, vật dụng tiện &iacute;ch rộng r&atilde;i ph&iacute;a sau. Cũng như c&aacute;c mẫu xe Mitsubishi kh&aacute;c, Triton đạt được t&iacute;nh năng vận h&agrave;nh h&agrave;ng đầu c&ugrave;ng với sự bền bỉ v&agrave; tin cậy so với c&aacute;c đối thủ c&ugrave;ng loại.</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	&nbsp;</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	Thiết kế b&ecirc;n ngo&agrave;i với những đường cong th&acirc;n xe nổi bật kiểu d&aacute;ng thể thao:</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	&nbsp;</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	Mặt trước với đ&egrave;n pha halogen k&iacute;nh trong kết hợp với đ&egrave;n sương m&ugrave; ốp viền m&agrave;u bạc c&ugrave;ng lưới tản nhiệt viền cr&ocirc;m nổi bật mạc hiệu 3 vi&ecirc;n kim cương lớn ở trung t&acirc;m thể hiện phong c&aacute;ch thể thao mạnh mẽ nhưng kh&ocirc;ng k&eacute;m phần thanh lịch.</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	Ốp v&egrave; lớn, K&iacute;nh chiếu hậu mạ cr&ocirc;m, Tay mở cửa mạ cr&ocirc;m, M&acirc;m b&aacute;nh xe mới 17-inch v&agrave; bệ bước b&ecirc;n h&ocirc;ng xe tạo h&igrave;nh ảnh mạnh mẽ cho thiết kế h&ocirc;ng xe.<br style="margin: 0px; padding: 0px;" />\r\n	Thiết kế cửa sau với những đường cong h&agrave;i ho&agrave; với th&ugrave;ng xe v&agrave; kiểu d&aacute;ng đ&egrave;n sau thanh lịch, g&oacute;p phần loại bỏ những n&eacute;t th&ocirc; cứng, mang đến những điểm nhấn mềm mại của d&ograve;ng xe du lịch.</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	Kh&ocirc;ng gian nội thất ấn tượng sang trọng v&agrave; nhiều tiện nghi b&ecirc;n trong ca-bin như d&ograve;ng xe du lịch:</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	Nội thất hai t&ocirc;ng m&agrave;u với kh&ocirc;ng gian cabin rộng tho&aacute;ng cho cả 5 người, trang bị ghế ngồi tiện nghi v&agrave; thoải m&aacute;i với nhiều trang thiết bị tiện dụng như hộp vật dụng trung t&acirc;m, g&aacute;c tay, gi&aacute; đỡ ly, hộp đựng k&iacute;nh m&aacute;t&hellip;</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	Kiểu d&aacute;ng thiết kế bảng điều khiển trung t&acirc;m ấn tượng với hệ thống điều h&ograve;a tự động, m&agrave;n h&igrave;nh hiển thị đa th&ocirc;ng tin cung cấp th&ocirc;ng tin về nhiệt độ, h&agrave;nh tr&igrave;nh, ti&ecirc;u hao nhi&ecirc;n liệu v&agrave; nhiều th&ocirc;ng tin tiện &iacute;ch kh&aacute;c.</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	C&ugrave;ng với v&ocirc; lăng 4 chấu, cần sang số bọc da sang trọng, ghế ngồi thoải m&aacute;i với tựa đầu lớn, kh&ocirc;ng gian ca-bin cao, khoảng để ch&acirc;n của h&agrave;ng ghế thứ hai thật rộng r&atilde;i v&agrave; g&aacute;c tay trung t&acirc;m với gi&aacute; để ly, tất cả đều khẳng định đẳng cấp của thiết kế nội thất Triton như d&ograve;ng xe du lịch sedan.</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	Triton c&oacute; những t&iacute;nh năng vận h&agrave;nh h&agrave;ng đầu của mẫu xe 2 cầu Mitsubishi nổi tiếng tr&ecirc;n đường đua Paris-Dakar, vừa kinh tế vừa hiệu quả:</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	Được trang bị động cơ Diesel 2.5L DI-D common rail mạnh mẽ, bền bỉ v&agrave; kinh tế, kh&ocirc;ng những ph&acirc;n phối m&ocirc;-men lớn ở tốc độ v&ograve;ng quay thấp m&agrave; c&ograve;n hoạt động &ecirc;m dịu, giảm kh&iacute; thải độc hại v&agrave; tiết kiệm nhi&ecirc;n liệu.</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	Hộp số s&agrave;n 5 số (hay Hộp số tự động 4 số) kết hợp với hệ thống truyền động 2 cầu - 4WD Easy select cho ph&eacute;p chuyển đổi linh hoạt từ một cầu chủ động (2H) sang 2 cầu chủ động (4H) ngay cả khi đang chạy ở tốc độ 100 km/h đảm bảo cho Triton vận h&agrave;nh mạnh mẽ v&agrave; hiệu quả trong mọi điều kiện địa h&igrave;nh.</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	Khung sắt xi cứng vững v&agrave; hệ thống treo trước độc lập tay nh&uacute;n k&eacute;p với l&ograve; xo xoắn, hệ thống treo sau sử dụng nh&iacute;p Triton vừa c&oacute; thể đ&aacute;p ứng việc chuy&ecirc;n chở được h&agrave;ng h&oacute;a với tải trọng lớn vừa vận h&agrave;nh ổn định tr&ecirc;n nhiều điều kiện mặt đường kh&aacute;c nhau.<br style="margin: 0px; padding: 0px;" />\r\n	Khoảng s&aacute;ng gầm xe cao 205mm Triton c&oacute; thể vận h&agrave;nh tr&ecirc;n đường ghồ ghề, ngập nước v&agrave; những điều kiện mặt đường kh&oacute; khăn kh&aacute;c.</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	Đồng thời Triton được ứng dụng những c&ocirc;ng nghệ v&agrave; Trang thiết bị an to&agrave;n ti&ecirc;n tiến nhất, kh&ocirc;ng hề thua k&eacute;m d&ograve;ng xe du lịch như l&agrave; khung xe RISE, th&acirc;n xe được gia cố những thanh hấp thụ chấn động bảo vệ h&agrave;nh kh&aacute;ch khỏi những va chạm từ ph&iacute;a trước v&agrave; hai b&ecirc;n, t&uacute;i kh&iacute; an to&agrave;n đ&ocirc;i kết hợp với d&acirc;y đai với cơ cấu căng đai tự động, hệ thống chống b&oacute; cứng phanh (ABS) kết hợp với cơ chế ph&acirc;n phối lực phanh điện tử (EBD).</div>\r\n<div align="justify" style="margin: 0px; padding: 0px; font-family: arial, Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(101, 101, 101);">\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	Mitsubishi Triton sẽ l&agrave; sự lựa chọn hiệu quả của kh&aacute;ch h&agrave;ng, kh&ocirc;ng chỉ đ&aacute;p ứng được sự tiện nghi, sang trọng, an to&agrave;n - phục vụ hiệu quả việc sản xuất, kinh doanh m&agrave; c&ograve;n mang lại những gi&acirc;y ph&uacute;t thư gi&atilde;n thật thoải m&aacute;i b&ecirc;n cạnh gia đ&igrave;nh, bạn b&egrave; trong những dịp mua sắm, những chuyến du lịch &ndash; th&aacute;m hiểm.</div>\r\n', '', '19', 14);
INSERT INTO `product` VALUES (14, '', 'Pajero Sport G.4WD.AT', '', 'pajero-sport-g4wdat', '1005000000', '', 'upload/images/5818366pajero_sport.jpg', '', 'Pajero Sport máy dầu, 1 cầu, số tự động.', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: arial, Arial, Helvetica, sans-serif; width: 100%;" width="520">\r\n	<tbody style="margin: 0px; padding: 0px;">\r\n		<tr style="margin: 0px; padding: 0px;">\r\n			<td style="margin: 0px; padding: 0px;" valign="top">\r\n				<p align="justify" style="margin: 0px; padding: 0px; line-height: 1.3;">\r\n					Pajero Sport ho&agrave;n to&agrave;n mới l&agrave; sự đột ph&aacute; về thiết kế, kh&ocirc;ng gian nội thất tiện nghi rộng r&atilde;i c&ugrave;ng khả năng sắp xếp linh hoạt c&aacute;c h&agrave;ng ghế để c&oacute; thể chở được nhiều dạng h&agrave;nh l&yacute; tiện lợi; t&iacute;nh năng vận h&agrave;nh ưu việt, độ bền bỉ v&agrave; sức mạnh vượt trội c&ugrave;ng t&iacute;nh năng off-road mạnh mẽ v&agrave; ti&ecirc;u chuẩn an to&agrave;n cao cấp.</p>\r\n				<div style="margin: 0px; padding: 0px;">\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3;">\r\n						Kiểu d&aacute;ng thể thao v&agrave; thanh lịch</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Pajero Sport g&acirc;y ấn tượng mạnh mẽ với kiểu d&aacute;ng trẻ trung, h&agrave;i h&ograve;a giữa đặc t&iacute;nh thể thao v&agrave; thiết kế thanh lịch, sang trọng.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Phần đầu xe nổi bật với lưới tản nhiệt dạng thanh k&eacute;p mạ cr&ocirc;m, mạc hiệu ba vi&ecirc;n kim cương lớn tạo n&eacute;t sang trọng, ấn tượng. Bề mặt rộng của cản trước, đ&egrave;n pha halogen dạng thấu k&iacute;nh kết hợp đ&egrave;n sương m&ugrave; viền bạc tạo n&ecirc;n phong c&aacute;ch thể thao mạnh mẽ.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Kiểu d&aacute;ng th&acirc;n xe thanh lịch c&ugrave;ng những đường cong h&agrave;i h&ograve;a tr&ecirc;n th&acirc;n xe với c&aacute;c chi tiết: gương chiếu hậu gập điện mạ cr&ocirc;m, tay nắm cửa mạ cr&ocirc;m, bệ bước h&ocirc;ng xe v&agrave; m&acirc;m hợp kim 17 inch kiểu thể thao.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Thiết kế ph&iacute;a sau xe l&agrave; sự kết hợp h&agrave;i h&ograve;a của cụm đ&egrave;n l&aacute;i thiết kế thanh lịch với cửa k&iacute;nh sậm m&agrave;u c&oacute; hệ thống x&ocirc;ng k&iacute;nh v&agrave; đ&egrave;n b&aacute;o phanh tr&ecirc;n cao.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						<span style="margin: 0px; padding: 0px; font-weight: bold;">Thiết kế nội thất tinh tế v&agrave; sang trọng, hướng đến sự tiện dụng tối ưu</span><img alt="" src="http://i43.photobucket.com/albums/e358/manhhagtvt/Pajerosportd2wdat_zpsd8ecb79a.jpg" style="margin: 5px 0px 10px 10px; padding: 0px; border: 0px solid; width: 270px; height: 163px; float: right;" /></p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Nội thất Pajero Sport được thiết kế h&agrave;i h&ograve;a với hai t&ocirc;ng m&agrave;u đen/x&aacute;m nhạt, nổi bật sự sang trọng, c&ugrave;ng bảng điều khiển trung t&acirc;m ốp gỗ - viền sơn m&agrave;u bạc, ốp gỗ - viền mạ cr&ocirc;m trang tr&iacute; cửa xe, v&ocirc; lăng v&agrave; cần số bọc da.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Kh&aacute;ch h&agrave;ng c&oacute; thể cảm nhận sự thoải m&aacute;i, tiện nghi trong từng chi tiết thiết kế của nội thất như hệ thống điều h&ograve;a 2 d&agrave;n lạnh: d&agrave;n lạnh trước tự động, d&agrave;n lạnh sau được bố tr&iacute; ở h&agrave;ng ghế thứ 3 gi&uacute;p kh&ocirc;ng gian sau xe lu&ocirc;n tho&aacute;ng m&aacute;t v&agrave; thư gi&atilde;n với DVD - m&agrave;n h&igrave;nh LCD cảm ứng Alpine cao cấp.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Nội thất của Pajero Sport được thiết kế rất kỹ lưỡng, hướng tới sự tiện dụng tối đa cho h&agrave;nh kh&aacute;ch. C&oacute; thể thấy r&otilde; điều n&agrave;y ở c&aacute;ch sắp xếp ghế m&agrave; &iacute;t c&oacute; mẫu xe SUV c&ugrave;ng ph&acirc;n kh&uacute;c c&oacute; được: h&agrave;ng ghế thứ 3 gấp phẳng xuống s&agrave;n, h&agrave;ng ghế thứ 2 c&oacute; khoảng trượt d&agrave;i, dễ d&agrave;ng gấp s&aacute;t h&agrave;ng ghế thứ 1, tạo kh&ocirc;ng gian chở h&agrave;nh l&yacute; rộng r&atilde;i. Ngo&agrave;i ra, xung quanh ghế ngồi được bố tr&iacute; g&aacute;c tay, ngăn chứa vật dụng, hộp đựng k&iacute;nh m&aacute;t, đ&egrave;n chiếu s&aacute;ng c&aacute;c cửa, hộc để ly, ổ cắm điện 12V&hellip; mang đến sự thuận tiện cho tất cả người ngồi tr&ecirc;n xe.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						T&iacute;nh năng vận h&agrave;nh mạnh mẽ, bền bỉ<img alt="" src="http://i43.photobucket.com/albums/e358/manhhagtvt/27_zpsec762ef8.jpg" style="margin: 5px 0px 10px 10px; padding: 0px; border: 0px solid; width: 229px; height: 154px; float: right;" /></p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Động cơ Diesel 2.5L Common Rail - Turbo với c&ocirc;ng suất 136 m&atilde; lực v&agrave; m&ocirc;men xoắn cực đại 32 kg.m &ndash; đảm bảo xe vận h&agrave;nh mạnh mẽ nhưng vẫn tiết kiệm nhi&ecirc;n liệu.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Hộp số s&agrave;n 5 cấp c&ugrave;ng hệ thống truyền động 4WD &ndash; Super Select l&agrave; lựa chọn hợp l&yacute; cho những người đam m&ecirc; sức mạnh v&agrave; kh&aacute;m ph&aacute;. Hệ thống 4WD-Super Select gi&uacute;p người điều khiển c&oacute; thể chuyển đổi dễ d&agrave;ng từ chế độ 1 cầu chủ động - 2WD sang 2 cầu chủ động - 4WD hay ngược lại ngay khi xe đang chạy tốc độ đến 100Km/h, đặc biệt l&agrave; chế độ 4HLc/ 4LLc kết hợp chức năng kh&oacute;a vi sai trung t&acirc;m sẽ cung cấp tối đa lực k&eacute;o chủ động đến 4 b&aacute;nh xe dẫn động gi&uacute;p xe vượt qua dễ d&agrave;ng mọi loại địa h&igrave;nh phức tạp.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Phi&ecirc;n bản hộp số tự động 4 cấp INVECS-II với chế độ điều khiển thể thao Sport Mode thừa sức thỏa m&atilde;n cảm gi&aacute;c sang số thể thao tr&ecirc;n nền tảng vận h&agrave;nh tiện nghi &ecirc;m &aacute;i của hộp số tự động INVECS II.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						<img alt="" src="http://i43.photobucket.com/albums/e358/manhhagtvt/PajeroSportsDS_1440x900_zps9abe52e1.jpg" style="margin: 5px 0px 10px 10px; padding: 0px; border: 0px solid; width: 229px; height: 141px; float: right;" />Hệ thống treo trước kết cấu tay nh&uacute;n k&eacute;p v&agrave; hệ thống treo sau của Pajero Sport sử dụng l&ograve; xo xoắn li&ecirc;n kết 3 điểm được tăng cường thanh c&acirc;n bằng lu&ocirc;n duy tr&igrave; sự vận h&agrave;nh ổn định kết hợp hệ thống l&aacute;i linh hoạt, phản ứng ch&iacute;nh x&aacute;c v&agrave; khung xe vững ch&atilde;i, tất cả sẽ hỗ trợ hiệu quả cho chuyến đi ấn tượng, cho d&ugrave; ở bất kỳ địa h&igrave;nh gồ ghề n&agrave;o hay ngay cả trong những điều kiện thời tiết thay đổi đột ngột.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Mặc d&ugrave; sở hữu thiết kế khoảng c&aacute;ch 2 cầu xe rộng, Pajero Sport c&oacute; b&aacute;n k&iacute;nh quay v&ograve;ng kh&aacute; ấn tượng 5,6 m&eacute;t, gi&uacute;p xe vận h&agrave;nh linh hoạt ngay cả tr&ecirc;n những khu vực chật hẹp.</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						T&iacute;nh năng an to&agrave;n cao cấp</p>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Pajero Sport được trang bị đầy đủ c&aacute;c thiết bị an to&agrave;n ti&ecirc;u chuẩn của d&ograve;ng xe SUV sang trọng, đảm bảo mang lại sự an to&agrave;n tối ưu cho h&agrave;nh kh&aacute;ch tr&ecirc;n xe:</p>\r\n					<ul style="margin: 0px 0px 10px 30px; padding-right: 0px; padding-left: 0px; text-align: justify;">\r\n						<li style="margin: 5px 0px 2px; padding: 0px;">\r\n							Cấu tr&uacute;c khung xe RISE cứng vững với khả năng chống va đập cao, khung xe được gia cố c&aacute;c điểm hấp thụ lực va chạm v&agrave; chuyển hướng chấn động đảm bảo an to&agrave;n cho h&agrave;nh kh&aacute;ch</li>\r\n						<li style="margin: 5px 0px 2px; padding: 0px;">\r\n							T&uacute;i kh&iacute; an to&agrave;n đ&ocirc;i kết hợp d&acirc;y đai với cơ cấu căng đai tự động</li>\r\n						<li style="margin: 5px 0px 2px; padding: 0px;">\r\n							Hệ thống chống b&oacute; cứng phanh ABS</li>\r\n						<li style="margin: 5px 0px 2px; padding: 0px;">\r\n							Hệ thống ph&acirc;n phối lực phanh điện tử EBD hoạt động ch&iacute;nh x&aacute;c, thay thế cho van cơ kh&iacute; LSPV</li>\r\n						<li style="margin: 5px 0px 2px; padding: 0px;">\r\n							Trục l&aacute;i v&agrave; b&agrave;n đạp phanh với cơ cấu tự đổ l&agrave;m giảm tối đa chấn thương cho người l&aacute;i trong c&aacute;c va chạm mạnh</li>\r\n					</ul>\r\n					<p style="margin: 0px 0px 8px; padding: 0px; line-height: 1.3; text-align: justify;">\r\n						Pajero Sport l&agrave; sự kết hợp h&agrave;i h&ograve;a giữa thiết kế thanh lịch thể thao v&agrave; t&iacute;nh năng SUV ưu việt danh tiếng của d&ograve;ng xe Pajero tr&ecirc;n đường đua Dakar Rally chắc chắn sẽ l&agrave;m thỏa m&atilde;n nhu cầu v&agrave; thị hiếu ng&agrave;y c&agrave;ng đa dạng v&agrave; tinh tế của kh&aacute;ch h&agrave;ng hiện nay.</p>\r\n				</div>\r\n			</td>\r\n		</tr>\r\n		<tr